class Solution:
    def longestPalindrome(self, s: str) -> int:
        d = {}
        for c in s:
            if c not in d:
                d[c] = 0
            d[c] += 1
        offest = 0
        max_len = 0
        for k,v in d.items():
            if v%2:
                max_len += v-1
                offest = 1
            else:
                max_len += v
        return max_len + offest
